mapper.select:
select device_class,mark, normal ,repairing, broken
from tb_DetectDevices d
where detect_id=#{detectId}

mapper.update:
update tb_DetectDevices
set normal=#{detectDevice.normal}, repairing=#{detectDevice.repairing},broken=#{detectDevice.broken}
where device_class=#{detectDevice.deviceClass} and detect_id=#{detectId}
